import React from 'react'
import './style.scss'
import AddToDo from './AddToDo'


class ListoDos extends React.Component{

    state = {
        listToDo:[
            {id: 1, title: "Play game"},
            {id: 2, title: "Learn japanese"},
            {id: 3, title: "Code project"},
        ]
    }

    addToDo = (toDo) => {
        this.setState({
            listToDo:[...this.state.listToDo, toDo]
        })
    }

    render() {

        let {listToDo} = this.state

        return (
            <>
                <div className='container'>
                    <AddToDo
                        addToDo = {this.addToDo}
                    />
                    <div className='list-todo'>
                        {listToDo && listToDo.length>0 && listToDo.map( (item,index) => {
                            return (
                                <div key={item.id}>
                                    <hr/>
                                        <div className='todo'>
                                            <span className='title'>{index + 1}: {item.title} </span>
                                            <div className='btn'>
                                                <button>Edit</button>
                                                <button>Delete</button>
                                            </div>
                                        </div>
                                    <hr/>
                                </div>
                            )
                        })}
                    </div>
                </div>
            </>
        )
    }
}

export default ListoDos
