import React from 'react';

class Components extends React.Component{

    state = {
        name: 'Trọng',
        channel: 'Trong TV'
    };

    ChangeName = (event) => {
        this.setState({
            name: event.target.value
        })
    }

    render(){ // Luôn trả về một khối 
        let name = 'My Components, Trọng';
        return (
            <>
            <div><input type='text' value={this.state.name} onChange={(event) => {this.ChangeName(event)}}> </input></div>
            <div>Hello { this.state.name }</div>
            <div>My chanel { this.state.channel }</div>
            </>
        );
    }

}

export default Components;
