import React from 'react';

class Components extends React.Component{

    state = {
        name: '',
        channel: 'Trong TV'
    };

    ChangeName = (event) => {
        this.setState({
            name: event.target.value
        })
    }

    ShowAlert = () => {
        alert('Bạn đã nhấn nút');
    }
    // Dùng arrow function

    render(){ // Luôn trả về một khối 
        return (
            <>
            <div><input type='text' value={this.state.name} onChange={(event) => {this.ChangeName(event)}} /></div>
            <div>Hello { this.state.name }</div>
            <div>My chanel { this.state.channel }</div>
            <div> <button onClick={() => {this.ShowAlert()}}> Click me!</button></div> 
            </>
        );
    }

}

export default Components;
