import React from 'react';
import ChildComponents from './ChildComponents';

class Components extends React.Component{

    // state = {
    //     name: '',
    //     channel: 'Trong TV'
    // };

    // ChangeName = (event) => {
    //     this.setState({
    //         name: event.target.value
    //     })
    // }

    // ShowAlert = () => {
    //     alert('Bạn đã nhấn nút');
    // }
    // Dùng arrow function

    state = {
        firstName: '',
        lastName: '',
        arrJobs: [
            {id: 'job 1', title: 'developer', salary: '500$'},
            {id: 'job 2', title: 'tester', salary: '400$'},
            {id: 'job 3', title: 'leader', salary: '600$'}
        ]
    }

    handleChangeFirstName = (event) => {
        this.setState({
            firstName: event.target.value
        })
    }

    handleChangeLastName = (event) => {
        this.setState({
            lastName: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault()
        console.log('Data: ',this.state)
    }

    render(){ 
        console.log(this.state)
        return (
            <>
    
            <form>
                <input type='text' placeholder='firstname' value={this.state.firstName} onChange={(event) => this.handleChangeFirstName(event)} /><br/>
                <input type='text' placeholder='lastname' value={this.state.lastName} onChange={(event) => this.handleChangeLastName(event)} /><br/>
                <input type='submit' value='Click me!' onClick={ (event) => this.handleSubmit(event)} />
            </form>

            <ChildComponents 
            name={this.state.firstName}
            age={'25'}
            arrJobs = {this.state.arrJobs}
            />

            </>
        );
    }

}

export default Components;
